import { createApp, h, provide } from "vue";
import AppLayout from "./layout/index.vue";
import router from "./router";
import store from "./store";
import helper from "./helpers";
import upperFirst from "lodash/upperFirst";
import camelCase from "lodash/camelCase";
import "./plugins/global-config";
import "./scss/app.scss";
import axios from "axios";
import VueAxios from "vue-axios";
import errorHandler from "./plugins/interceptor";
import VueJsBridge, { $fetch } from "vue-bridge-gateway";
import { ApolloClient, InMemoryCache } from "@apollo/client";
import { DefaultApolloClient } from "@vue/apollo-composable";

import {
  Button,
  Cell,
  CellGroup,
  Divider,
  Form,
  Field,
  List,
  Empty,
  Toast,
  Calendar,
} from "vant";

const defaultClient = new ApolloClient({
  uri: "https://rickandmortyapi.com/graphql",
  cache: new InMemoryCache(),
});

const app = createApp({
  setup() {
    provide(DefaultApolloClient, defaultClient);
  },
  render() {
    return h(AppLayout);
  },
});
app.config.devtools = true;

// vant
app
  .use(Button)
  .use(Cell)
  .use(CellGroup)
  .use(Divider)
  .use(Form)
  .use(Field)
  .use(List)
  .use(Empty)
  .use(Toast)
  .use(Calendar);

const requireComponent = require.context(
  "./components",
  true,
  /(st-)[\w-]+\.vue$/
);
requireComponent.keys().forEach((fileName) => {
  // Get component config
  const componentConfig = requireComponent(fileName);
  const componentName = upperFirst(
    camelCase(
      fileName.replace(/^\.\/ui\/|^\.\/ui-custom\//, "").replace(/\.\w+$/, "")
    )
  );
  app.component(componentName, componentConfig.default || componentConfig);
});
errorHandler($fetch);

app.use(VueJsBridge);
app.use(VueAxios, axios);
app.mixin(helper).use(router).use(store).mount("#app");
